INSERT into recipes (title, description, image_url, cat_code, cuisine, calories, rate, servings, cook_time, skill_level, source) VALUES ('Spinach Spaetzle With Bacon and Sage','Spaetzle, the delicious little German dumplings (sometimes called batter noodles).These are green spaetzle, made with spinach purée, sizzled with bacon and sage leaves. (Instead of spinach, you could add chopped herbs, but plain spaetzle are divine, too.) Spaetzle take only moments to cook and can be prepared in advance, then sautéed in butter to serve.','21KITCHEN2-articleLarge','Pasta','German','','Delicious',6,'1 hour','M','NYT');
